/**
 * Checks if the input is defined
 * @category Is
 */
export declare function isDef<T = any>(v?: T): v is T;
/**
 * Checks if the input is a boolean
 * @category Is
 */
export declare function isBoolean(v: unknown): v is boolean;
/**
 * Checks if the input is a function.
 * @category Is
 */
export declare function isFunction<T extends Function>(v: unknown): v is T;
/**
 * Checks if the input is a number
 * @category Is
 */
export declare function isNumber(v: unknown): v is number;
/**
 * Checks if the input is a string
 * @category Is
 */
export declare function isString(v: unknown): v is string;
/**
 * Checks if the input is a symbol
 * @category Is
 */
export declare function isSymbol(v: unknown): v is symbol;
/**
 * Checks if the input is an object
 * @category Is
 */
export declare function isObject(v: unknown): v is Record<PropertyKey, unknown>;
/**
 * Checks if the input is an array
 * @category Is
 */
export declare function isArray<T>(v: unknown): v is T[];
/**
 * Checks if the input is undefined
 */
export declare function isUndefined(v: unknown): v is undefined;
/**
 * Checks if the input is null
 * @category Is
 */
export declare function isNull(v: unknown): v is null;
/**
 * Checks if the input is a regexp
 * @category Is
 */
export declare function isRegexp(v: unknown): v is RegExp;
/**
 * Checks if the input is a date
 * @category Is
 */
export declare function isDate(v: unknown): v is Date;
/**
 * Checks if the input is an empty object
 * @category Is
 */
export declare function isEmptyObject(v: unknown): boolean;
/**
 * Checks if the input is a window
 * @category Is
 */
export declare function isWindow(v: unknown): boolean;
/**
 * Checks if the input is a browser
 * @category Is
 */
export declare function isBrowser(): boolean;
