export const slash = (s) => s.replace(/\\/g, "/");
export function capitalize(s) {
  return s[0].toUpperCase() + s.slice(1).toLowerCase();
}
export function ensurePrefix(prefix, str) {
  if (str.startsWith(prefix))
    return str;
  return prefix + str;
}
export function ensureSuffix(suffix, str) {
  if (str.endsWith(suffix))
    return str;
  return str + suffix;
}
export function kebabCase(str) {
  return str.replace(/\s+/g, "-").replace(/([a-z])([A-Z])/g, "$1-$2").toLowerCase();
}
export function camelCase(str) {
  return str.replace(/(?:-|\s)(\w)/g, (_, m) => m.toUpperCase());
}
